#!/usr/bin/env node
const fs=require("fs"),path=require("path"),envPath=path.resolve(process.cwd(),".env"),envExamplePath=path.resolve(process.cwd(),".env.example"),envRegex=/(^\s*\S*?=).*$/gm;try{if(fs.existsSync(envPath)){const e=fs.readFileSync(envPath,{encoding:"utf8",flag:"r"});fs.writeFileSync(envExamplePath,e.replaceAll(envRegex,"$1")),console.log(".env.example generated")}else console.log(".env not found in"+process.cwd())}catch(e){console.error(e)}
